import React, { useEffect, useState } from 'react';

export const Recherche = () => {

  const [nomproduit, setNomproduit] = useState('');
  const [idcategorie, setIdcategorie] = useState('');
  const [prixmin, setPrixmin] = useState('');
  const [error, setError] = useState('');
  const [items, setItems] = useState([]);
  const [result, setResult] = useState([]);

  let url = "http://192.168.43.146:8080/Categorie";
  useEffect(() => { 
    fetch(url,{ 
      method: "GET",
      headers: { 'Content-Type': 'application/json' }
    }).then((response)=>response.json())
    .then((json)=>setItems(json.data));
  },[]);

  const rechercher = (event) => {
    event.preventDefault();
    const Recherche = {nomproduit,idcategorie,prixmin};
    useEffect(() => {
      fetch('http://192.168.43.146:8080/produit/recherche',{
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(Recherche)
      }).then((response)=>response.json())
      .then((json)=>setResult(json.data));
    },[]);

  return (
    <>
    <h2>Recherche avancer</h2>
    <form onSubmit={rechercher}>
      <label>
        Nom produit:
        <input type="text" value={nomproduit} onChange={(event) => setNomproduit(event.target.value)} />
      </label>
      <br />
      <label>
        Categorie:
        <select value={idcategorie} onChange={(event) => setIdcategorie(event.target.value)}>
        {items.map((item) => {
          return ( 
            <option value={item.idcategorie}>{item.nomcategorie}</option>
          )
        })}
        </select>
      </label><br />
      <label>
        Prix max:
        <input type="number" value={prixmin} onChange={(event) => setPrixmin(event.target.value)} />
      </label>
      <br />
      {error}
      <button type="submit">Rechercher</button>
    </form>
    <h2>Resultat recherche</h2>
    <table border="1">
        <thead>
            <tr>
              <th>Produit</th>
              <th>Categorie</th>
              <th>Client</th>
              <th>Email</th>
              <th>Prix</th>
              <th>Date enchere</th>
            </tr>
        </thead>
        <tbody>

        </tbody>
    </table>
    </>
  );
}
}
export default Recherche;
