import React, { useEffect, useState } from 'react';

export const ListeEnchere = () => {

    const [items, setItems] = useState([]);
    const [id, setId] = useState('');

  const detail = (event) => {
    localStorage.setItem('enchere',JSON.stringify(items));
    window.location.replace('/FicheEnchere');
  }

  useEffect(() => {
    fetch('http://192.168.20.4:8080/ProduitTsyclient',
    { method: "GET" })
    .then((response)=>response.json())
    .then((json)=>setItems(json.data));
  },[]);

  return (
    <>
    <h2>Liste des Enchere en cours</h2>
    <form onSubmit={detail}>
      <table border="1">
        <thead>
          <tr>
              <th>ID</th>
              <th>Produit</th>
              <th>Categorie</th>
              <th>Montant Enchere</th>
          </tr>
          </thead>
          <tbody>
            {items.map((item) => {
                return (
                    <tr>
                        <td>{item.id}</td>
                        <td>{item.nomproduit}</td>
                        <td>{item.nomcategorie}</td>
                        <td>{item.prixmin}</td>
                        <input type="hidden" value={id} onChange={(event) => setId(item.id)} />
                        <td><input type="submit" value="detail" onClick={detail}/></td>
                    </tr>
                )
            })}
          </tbody>
      </table>
    </form>
    </>
  );
}

export default ListeEnchere;
