import React, { useState } from 'react';

export const Demande_rechargement = () => {

    const [montant, setMontant] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        fetch('http://192.168.20.132:8080/DemandeRechargement/save',{
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ idclient: localStorage.getItem('idc'),idproduit: localStorage.getItem, montant: montant })
        })
        .then(response => {
          if (!response.ok) {
            setError('Identifiant ou mot de passe incorrect');
            throw Error(response.statusText);
          }
          return response.json();
        })
        .then(data => {
          //mety
        })
        .catch(error => console.log(error));
      };

    return (
        <form onSubmit={handleSubmit}>
          <label>
            Montant:
            <input type="text" value={montant} onChange={(event) => setMontant(event.target.value)} />
          </label>
          <button type="submit">inserer</button>
        </form>
      );
}

export default Demande_rechargement;
