import React, { useEffect, useState } from 'react';

export const ListeEnchere = () => {

    const [items, setItems] = useState([]);
    const [id, setId] = useState('');

  const handleSubmit = (event) => {
    console.log("ato");
  }

  useEffect(() => {
    fetch('http://192.168.20.4:8080/ProduitTsyclient',
    { method: "GET" })
    .then((response)=>response.json())
    .then((json)=>setItems(json.data));
  },[]);

  return (
    <>
    <h2>Liste des Enchere en cours</h2>
    <form onSubmit={handleSubmit}>
      <button type="submit" onClick="handleSubmit">Detail</button>
    </form>
      <table border="1">
        <thead>
          <tr>
              <th>ID</th>
              <th>Produit</th>
              <th>Categorie</th>
              <th>Montant Enchere</th>
          </tr>
          </thead>
          <tbody>
            {items.map((item) => {
                return (
                    <tr>
                        <td>{item.id}</td>
                        <td>{item.nomproduit}</td>
                        <td>{item.nomcategorie}</td>
                        <td>{item.prixmin}</td>
                        <td><input type="hidden" value={id} onChange={(event) => setId(item.id)} /></td>
                    </tr>
                )
            })}
          </tbody>
      </table>
    </>
  );
}

export default ListeEnchere;
