import React, { useEffect, useState } from 'react';

export const FicheEnchere = () => {

    const [items, setItems] = useState([]);


  useEffect(() => {
    fetch("http://192.168.43.146:8080/Produit/"+localStorage.getItem('id'),{ 
      method: "GET"
     })
     .then((response)=>response.json())
     .then((json)=>setItems(json.data));
  },[]);

  return (
    <>
    <h2>Fiche Enchere</h2>
    <table border="1">
        <thead>
          <tr>
            <th>Produit</th>
            <th>Categorie</th>
            <th>Prix</th>
            <th>Date enchere</th>
          </tr>
        </thead>
        <tbody>
        {items.map((item) => {
            return (
                <>
                <p>ID: {item.id}</p>
                <p>idProduit: {item.idproduit}</p>
                <p>idPlient: {item.idclient}</p>
                <p>Montant enchere: {item.montantenchere}</p>
                <p>Produit: {item.produit}</p>
                <p>duree: {item.duree}</p>
                <p>dateenchere: {item.dateenchere}</p>
                <p>prixmin: {item.prixmin}</p>
                <p>client: {item.client}</p>
                <p>email: {item.email}</p>
                </>
            )
        })}
        </tbody>
        </table>
    </>
  );
}

export default FicheEnchere;
