import React, { useState } from 'react';

export const Login = () => {
  const [email, setEmail] = useState('');
  const [motdepasse, setMotdepasse] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    const Client = {email,motdepasse};
    fetch('http://192.168.20.4:8080/Client/login',{
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(Client)
    })
    .then(response => {
      if (!response.ok) {
        setError('Identifiant ou mot de passe incorrect');
        throw Error(response.statusText);
      }
      return response.json();
    })
    .then(data => {
      if(data.data=="Login invalid"){
        setError(data.data);
      } else{
        window.location.replace('/demanderechargement');
      }
    })
    .catch(error => console.log(error));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input type="text" value={email} onChange={(event) => setEmail(event.target.value)} />
      </label>
      <br />
      <label>
        Mot de passe:
        <input type="password" value={motdepasse} onChange={(event) => setMotdepasse(event.target.value)} />
      </label>
      <br />

      <button type="submit">Se connecter</button>
    </form>
  );
}

export default Login;
