import React, { useState } from 'react';

export const Demande_rechargement = () => {

    const [montantenchere, setMontantenchere] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        const Enchere = {}
        fetch('http://192.168.43.146:8080/enchere/save',{
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ idclient: localStorage.getItem('idc'),idproduit: localStorage.getItem('id'), montantenchere: montantenchere })
        })
        .then(response => {
          if (!response.ok) {
            setError('Identifiant ou mot de passe incorrect');
            throw Error(response.statusText);
          }
          return response.json();
        })
        .then(data => {
          setError("Encherer inserer avec succès");
        })
        .catch(error => console.log(error));
      };

    return (
      <>
      <p><a h></a></p>
      <h2>Encherir le produit</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Montant:
            <input type="text" value={montantenchere} onChange={(event) => setMontantenchere(event.target.value)} />
          </label>
          <p>{error}</p>
          <button type="submit">inserer</button>
        </form>
        </>
      );
}

export default Demande_rechargement;
