export const ListeEnchere = () => {

    const [items, setItems] = useState([]);
    const [id, setId] = useState('');

  const detail = (event) => {

  }

  useEffect(() => {
    fetch('http://192.168.20.4:8080/ProduitTsyclient',
    { method: "GET" })
    .then((response)=>response.json())
    .then((json)=>setItems(json.data));
  },[]);

  return (
    <>
    <h2>Liste des Enchere en cours</h2>
    <table border="1">
      <thead>
        <tr>
            <th>ID</th>
            <th>IdProduit</th>
            <th>IdClient</th>
            <th>Montant Enchere</th>
        </tr>
        </thead>
        <tbody>
          {items.map((item) => {
              return (
                <form onSubmit={detail}>
                    <tr>
                        <td>{item.id}</td>
                        <td>{item.idproduit}</td>
                        <td>{item.idclient}</td>
                        <td>{item.montantenchere}</td>
                        <input type="hidden" value={email} onChange={(id) => setId(item)} />
                        <td>Detail</td>
                    </tr>
                  </form>
              )
          })}
        </tbody>
    </table>
    </>
  );
}

export default ListeEnchere;
