import React, { useEffect, useState } from 'react';

export const ListeEnchere = () => {

    const [items, setItems] = useState([]);


  useEffect(() => {
    fetch('http://192.168.20.4:8080/Enchere',
    { method: "GET" })
    .then((response)=>response.json())
    .then((json)=>setItems(json.data));
  },[]);

  return (
    <>
    <h2>Liste des Enchere</h2>
    <table border="1">
        <tr>
            <th>ID</th>
            <th>IdProduit</th>
            <th>IdClient</th>
            <th>Montant Enchere</th>
        </tr>
        {items.map((item) => {
            return (
                <tr>
                    <td>{item.id}</td>
                    <td>{item.idproduit}</td>
                    <td>{item.idclient}</td>
                    <td>{item.montantenchere}</td>
                </tr>
            )
        })}
    </table>
    </>
  );
}

export default ListeEnchere;
