import React, { useEffect, useState } from 'react';

import './assets/pages/waves/css/waves.min.css';
import './assets/css/bootstrap/css/bootstrap.min.css';
import './assets/pages/waves/css/waves.min.css';
import './assets/icon/font-awesome/css/font-awesome.min.css';
import './assets/icon/icofont/css/icofont.css';
import im1 from './assets/images/logo.png';
import './assets/js/popper.js/popper.min.js';
import './assets/js/bootstrap/js/bootstrap.min.js';
import './assets/pages/waves/js/waves.min.js';
import './assets/js/jquery-slimscroll/jquery.slimscroll.js';

export const ListeEnchere = () => {

    const [items, setItems] = useState([]);
    const [id, setId] = useState('');

    let url = "http://192.168.43.146:8080/ProduitTsyclient";
    useEffect(() => {
        fetch(url,{ 
        method: "GET",
        headers: { 'Content-Type': 'application/json' }
        }).then((response)=>response.json())
        .then((json)=>setItems(json.data));
    },[]);

    const detail = (id) => {
      console.log("detail");
      console.log('id: '+id);
      localStorage.setItem('id',id);
      window.location.replace('/FicheEnchere');
  }

    return (
      <>
          <table border="1">
            <thead>
              <tr>
                <th>Produit</th>
                <th>Categorie</th>
                <th>Prix</th>
                <th>Date enchere</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => {
                return (
                <tr>
                  <td>{item.nomproduit}</td>
                  <td>{item.nomcategorie}</td>
                  <td>{item.prixmin}</td>
                  <td>{item.dateenchere}</td>
                  <td><button type="submit" onClick={() => detail(item.id)}>Detail</button></td>
                </tr>
                )
              })}
            </tbody>
          </table>
    </>
    );

}

export default ListeEnchere;
