import React, { useEffect, useState } from 'react';

export const ListeEnchere = () => {

    const [items, setItems] = useState([]);
    const [id, setId] = useState('');

    let url = "http://192.168.43.146:8080/ProduitTsyclient";
    useEffect(() => {
        fetch(url,{ 
        method: "GET",
        headers: { 'Content-Type': 'application/json' }
        }).then((response)=>response.json())
        .then((json)=>setItems(json.data));
    },[]);

    const detail = (id) => {
      console.log("detail");
      console.log('id: '+id);
      localStorage.setItem('id',id);
      window.location.replace('/FicheEnchere');
  }

    return (
      <>
      <body class="nav-md">
        <div class="container body">
          <div class="main_container">
            <div class="col-md-3 left_col">
              <div class="left_col scroll-view">
                <div class="navbar nav_title">
                  <a href="index.html" class="site_title"><i class="fa fa-paw"></i> <span>Gentelella Alela!</span></a>
                </div>
                <table border="1">
                  <thead>
                    <tr>
                      <th>Produit</th>
                      <th>Categorie</th>
                      <th>Prix</th>
                      <th>Date enchere</th>
                    </tr>
                  </thead>
                  <tbody>
                    {items.map((item) => {
                      return (
                      <tr>
                        <td>{item.nomproduit}</td>
                        <td>{item.nomcategorie}</td>
                        <td>{item.prixmin}</td>
                        <td>{item.dateenchere}</td>
                        <td><button type="submit" onClick={() => detail(item.id)}>Detail</button></td>
                      </tr>
                      )
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </body>
    </>
    );

}

export default ListeEnchere;
